import React, { useState } from 'react';
import { superAdminAPI } from '../api/apiService';

const RegistrationDetailModal = ({ registration, onClose, onUpdate }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    status: registration?.status || '',
    role: registration?.role || ''
  });

  const handleStatusChange = async (newStatus) => {
    try {
      if (newStatus === 'APPROVED') {
        await superAdminAPI.approveUser(registration.id, registration.role);
      } else if (newStatus === 'REJECTED') {
        await superAdminAPI.rejectUser(registration.id, 'Rejected by admin');
      } else {
        // For other status changes (like PENDING), use the status update endpoint
        await superAdminAPI.updateUserStatus(registration.id, newStatus);
      }
      
      setFormData(prev => ({ ...prev, status: newStatus }));
      onUpdate && onUpdate();
      alert(`Registration ${newStatus.toLowerCase()} successfully!`);
    } catch (error) {
      console.error('Error updating registration status:', error);
      alert(`Failed to update registration status: ${error.response?.data?.message || error.message}`);
    }
  };

  const handleRoleChange = async (newRole) => {
    try {
      await superAdminAPI.updateUser(registration.id, { role: newRole });
      setFormData(prev => ({ ...prev, role: newRole }));
      onUpdate && onUpdate();
      alert('Role updated successfully!');
    } catch (error) {
      console.error('Error updating role:', error);
      alert(`Failed to update role: ${error.response?.data?.message || error.message}`);
    }
  };

  if (!registration) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content registration-detail-modal">
        <div className="modal-header">
          <h2 className="modal-title">Registration Details</h2>
          <button className="modal-close" onClick={onClose} aria-label="Close modal">Ã—</button>
        </div>
        
        <div className="modal-body">
          <div className="registration-info">
            {/* Personal Information Section */}
            <div className="info-section personal-info-section">
              <h3 className="section-title">
                <i className="fas fa-user"></i>
                Personal Information
              </h3>
              <div className="info-grid">
                <div className="info-item">
                  <label className="info-label">Full Name</label>
                  <span className="info-value">{registration.name}</span>
                </div>
                <div className="info-item">
                  <label className="info-label">Email Address</label>
                  <span className="info-value">{registration.email}</span>
                </div>
                <div className="info-item">
                  <label className="info-label">Phone Number</label>
                  <span className="info-value">{registration.phoneNumber}</span>
                </div>
                <div className="info-item">
                  <label className="info-label">Gender</label>
                  <span className="info-value">{registration.gender}</span>
                </div>
                <div className="info-item">
                  <label className="info-label">Date of Birth</label>
                  <span className="info-value">{registration.dateOfBirth}</span>
                </div>
              </div>
            </div>

            {/* Account Information Section */}
            <div className="info-section account-info-section">
              <h3 className="section-title">
                <i className="fas fa-shield-alt"></i>
                Account Information
              </h3>
              <div className="info-grid">
                <div className="info-item">
                  <label className="info-label">User Role</label>
                  <div className="editable-field">
                    {isEditing ? (
                      <select 
                        value={formData.role} 
                        onChange={(e) => handleRoleChange(e.target.value)}
                        className="form-select role-select"
                      >
                        <option value="FARMER">Farmer</option>
                        <option value="EMPLOYEE">Employee</option>
                        <option value="ADMIN">Admin</option>
                        <option value="SUPER_ADMIN">Super Admin</option>
                      </select>
                    ) : (
                      <span className={`status-badge role-${registration.role.toLowerCase()}`}>
                        {registration.role}
                      </span>
                    )}
                  </div>
                </div>
                <div className="info-item">
                  <label className="info-label">Account Status</label>
                  <div className="editable-field">
                    {isEditing ? (
                      <select 
                        value={formData.status} 
                        onChange={(e) => handleStatusChange(e.target.value)}
                        className="form-select status-select"
                      >
                        <option value="PENDING">Pending</option>
                        <option value="APPROVED">Approved</option>
                        <option value="REJECTED">Rejected</option>
                      </select>
                    ) : (
                      <span className={`status-badge status-${registration.status.toLowerCase()}`}>
                        {registration.status}
                      </span>
                    )}
                  </div>
                </div>
                <div className="info-item">
                  <label className="info-label">KYC Status</label>
                  <span className={`status-badge kyc-${registration.kycStatus?.toLowerCase() || 'pending'}`}>
                    {registration.kycStatus || 'PENDING'}
                  </span>
                </div>
                <div className="info-item">
                  <label className="info-label">Registration Date</label>
                  <span className="info-value">{new Date(registration.createdAt).toLocaleDateString()}</span>
                </div>
              </div>
            </div>

            {/* Location Information Section */}
            {registration.state && (
              <div className="info-section location-info-section">
                <h3 className="section-title">
                  <i className="fas fa-map-marker-alt"></i>
                  Location Information
                </h3>
                <div className="info-grid">
                  <div className="info-item">
                    <label className="info-label">State</label>
                    <span className="info-value">{registration.state}</span>
                  </div>
                  <div className="info-item">
                    <label className="info-label">District</label>
                    <span className="info-value">{registration.district}</span>
                  </div>
                  <div className="info-item">
                    <label className="info-label">Region</label>
                    <span className="info-value">{registration.region}</span>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
        
        <div className="modal-footer">
          <div className="footer-actions">
            <button 
              className={`action-btn ${isEditing ? 'btn-secondary' : 'btn-primary'}`}
              onClick={() => setIsEditing(!isEditing)}
            >
              <i className={`fas ${isEditing ? 'fa-times' : 'fa-edit'}`}></i>
              {isEditing ? 'Cancel Editing' : 'Edit Registration'}
            </button>
            <button className="action-btn btn-close" onClick={onClose}>
              <i className="fas fa-times"></i>
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RegistrationDetailModal;
