import React, { useEffect, useState } from 'react';
import '../styles/ViewFarmerDetails.css';

const ViewEmployee = ({ employeeData, onBack, onSave }) => {
	const [isEditMode, setIsEditMode] = useState(false);
	const [formData, setFormData] = useState({});

	useEffect(() => {
		if (employeeData) {
			setFormData({
				firstName: employeeData.firstName || '',
				lastName: employeeData.lastName || '',
				email: employeeData.email || '',
				phone: employeeData.phone || employeeData.contactNumber || '',
				dateOfBirth: employeeData.dateOfBirth || employeeData.dob || '',
				gender: employeeData.gender || '',
				role: employeeData.role || employeeData.role?.name || '',
				designation: employeeData.designation || '',
				status: employeeData.status || employeeData.accessStatus || 'ACTIVE',
				city: employeeData.city || employeeData.district || '',
				state: employeeData.state || '',
				pincode: employeeData.pincode || employeeData.zipcode || '',
				address: employeeData.address || ''
			});
		}
	}, [employeeData]);

	const safe = (value, fallback = 'Not provided') => {
		if (value === null || value === undefined) return fallback;
		const str = String(value).trim();
		return str === '' ? fallback : str;
	};

	const handleInputChange = (field, value) => {
		setFormData(prev => ({ ...prev, [field]: value }));
	};

	const handleSave = async () => {
		try {
			await onSave?.(formData);
			setIsEditMode(false);
		} catch (e) {
			console.error('Error saving employee:', e);
			alert('Failed to save employee data');
		}
	};

	const handleCancel = () => {
		setIsEditMode(false);
		if (employeeData) {
			setFormData({
				firstName: employeeData.firstName || '',
				lastName: employeeData.lastName || '',
				email: employeeData.email || '',
				phone: employeeData.phone || employeeData.contactNumber || '',
				dateOfBirth: employeeData.dateOfBirth || employeeData.dob || '',
				gender: employeeData.gender || '',
				role: employeeData.role || employeeData.role?.name || '',
				designation: employeeData.designation || '',
				status: employeeData.status || employeeData.accessStatus || 'ACTIVE',
				city: employeeData.city || employeeData.district || '',
				state: employeeData.state || '',
				pincode: employeeData.pincode || employeeData.zipcode || '',
				address: employeeData.address || ''
			});
		}
	};

	const renderField = (label, field, type = 'text', options = []) => {
		if (isEditMode) {
			if (type === 'select') {
				return (
					<select
						value={formData[field] || ''}
						onChange={(e) => handleInputChange(field, e.target.value)}
						className="edit-input"
					>
						{options.map(option => (
							<option key={option.value} value={option.value}>
								{option.label}
							</option>
						))}
					</select>
				);
			}
			if (type === 'textarea') {
				return (
					<textarea
						value={formData[field] || ''}
						onChange={(e) => handleInputChange(field, e.target.value)}
						className="edit-input"
					/>
				);
			}
			return (
				<input
					type={type}
					value={formData[field] || ''}
					onChange={(e) => handleInputChange(field, e.target.value)}
					className="edit-input"
				/>
			);
		}
		return <span>{safe(employeeData?.[field])}</span>;
	};

	return (
		<div className="view-farmer-content">
			<div className="view-farmer-header">
				<button className="back-btn" onClick={onBack}>‚Üê Back to Employees</button>
				<h2 style={{ margin: 0, flex: 1, textAlign: 'center' }}>Employee Details</h2>
				<div style={{ display: 'flex', gap: '8px' }}>
					{!isEditMode ? (
						<button onClick={() => setIsEditMode(true)} style={{ background: '#10b981', color: 'white', border: 'none', borderRadius: '6px', padding: '6px 12px', cursor: 'pointer', fontSize: '14px' }}>Edit</button>
					) : (
						<>
							<button onClick={handleSave} style={{ background: '#10b981', color: 'white', border: 'none', borderRadius: '6px', padding: '6px 12px', cursor: 'pointer', fontSize: '14px' }}>Save</button>
							<button onClick={handleCancel} style={{ background: '#6b7280', color: 'white', border: 'none', borderRadius: '6px', padding: '6px 12px', cursor: 'pointer', fontSize: '14px' }}>Cancel</button>
						</>
					)}
				</div>
			</div>

			<div className="view-farmer-body">
				<div className="farmer-details-container">
					<div className="detail-section">
						<h3>Personal Information</h3>
						<div className="detail-grid">
							<div className="detail-item"><label>First Name:</label>{renderField('First Name', 'firstName')}</div>
							<div className="detail-item"><label>Last Name:</label>{renderField('Last Name', 'lastName')}</div>
							<div className="detail-item"><label>Email:</label>{renderField('Email', 'email')}</div>
							<div className="detail-item"><label>Phone:</label>{renderField('Phone', 'phone')}</div>
							<div className="detail-item"><label>Date of Birth:</label>{renderField('Date of Birth', 'dateOfBirth', 'date')}</div>
							<div className="detail-item"><label>Gender:</label>{renderField('Gender', 'gender', 'select', [
								{ value: '', label: 'Select Gender' },
								{ value: 'Male', label: 'Male' },
								{ value: 'Female', label: 'Female' },
								{ value: 'Other', label: 'Other' }
							])}</div>
						</div>
					</div>

					<div className="detail-section">
						<h3>Employment</h3>
						<div className="detail-grid">
							<div className="detail-item"><label>Role:</label>{renderField('Role', 'role', 'select', [
								{ value: '', label: 'Select Role' },
								{ value: 'employee', label: 'Employee' },
								{ value: 'admin', label: 'Admin' },
								{ value: 'super_admin', label: 'Super Admin' }
							])}</div>
							<div className="detail-item"><label>Designation:</label>{renderField('Designation', 'designation')}</div>
							<div className="detail-item"><label>Status:</label>{renderField('Status', 'status', 'select', [
								{ value: 'ACTIVE', label: 'Active' },
								{ value: 'INACTIVE', label: 'Inactive' },
								{ value: 'PENDING', label: 'Pending' }
							])}</div>
						</div>
					</div>

					<div className="detail-section">
						<h3>Address</h3>
						<div className="detail-grid">
							<div className="detail-item"><label>City:</label>{renderField('City', 'city')}</div>
							<div className="detail-item"><label>State:</label>{renderField('State', 'state')}</div>
							<div className="detail-item"><label>Pincode:</label>{renderField('Pincode', 'pincode')}</div>
							<div className="detail-item full-width"><label>Complete Address:</label>{renderField('Address', 'address', 'textarea')}</div>
						</div>
					</div>
				</div>
			</div>
		</div>
	);
};

export default ViewEmployee;
