import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { farmersAPI, employeesAPI, superAdminAPI, adminAPI } from '../api/apiService';
import DataTable from '../components/DataTable';

import RegistrationApprovalModal from '../components/RegistrationApprovalModal';
import RegistrationDetailModal from '../components/RegistrationDetailModal';
import ViewFarmerRegistrationDetails from '../components/ViewFarmerRegistrationDetails';
import AssignmentModal from '../components/AssignmentModal';
import FarmerForm from '../components/FarmerForm';
import ViewEditEmployeeDetails from '../components/ViewEditEmployeeDetails';
import EmployeeRegistrationForm from '../components/EmployeeRegistrationForm';
import KYCDocumentUpload from '../components/KYCDocumentUpload';
import DeleteModal from '../components/DeleteModal';
import UserProfileDropdown from '../components/UserProfileDropdown';
import '../styles/Dashboard.css';

const SuperAdminDashboard = () => {
  const { user, logout } = useAuth();

  // Debug logging
  console.log('SuperAdminDashboard - User data:', user);
  console.log('SuperAdminDashboard - User name:', user?.name);
  console.log('SuperAdminDashboard - User role:', user?.role);
  console.log('SuperAdminDashboard - User email:', user?.email);
  console.log('=== SUPER ADMIN DASHBOARD LOADED ===');

  // Test if user data is available
  useEffect(() => {
    console.log('=== USER DATA CHECK ===');
    console.log('User in useEffect:', user);
    console.log('User name in useEffect:', user?.name);
    console.log('User role in useEffect:', user?.role);
    console.log('Greeting text:', getGreeting());
  }, [user]);


  const [activeTab, setActiveTab] = useState('dashboard');
  const [farmers, setFarmers] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [registrations, setRegistrations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Greeting function based on time of day
  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour >= 5 && hour < 12) {
      return 'Good Morning';
    } else if (hour >= 12 && hour < 17) {
      return 'Good Afternoon';
    } else if (hour >= 17 && hour < 21) {
      return 'Good Evening';
    } else {
      return 'Good Night';
    }
  };

  // Modal states

  const [showFarmerDetails, setShowFarmerDetails] = useState(false);
  const [selectedFarmer, setSelectedFarmer] = useState(null);
  const [showAssignmentModal, setShowAssignmentModal] = useState(false);
  const [showFarmerForm, setShowFarmerForm] = useState(false);
  const [showEmployeeDetails, setShowEmployeeDetails] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [showEmployeeRegistration, setShowEmployeeRegistration] = useState(false);
  const [showKYCModal, setShowKYCModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);
  const [editingFarmer, setEditingFarmer] = useState(null);
  const [registrationFilters, setRegistrationFilters] = useState({
    role: '',
    status: ''
  });

  const [filters, setFilters] = useState({
    state: '',
    district: '',
    region: '',
    kycStatus: '',
    assignmentStatus: '',
    employeeFilter: ''
  });

  const [employeeFilters, setEmployeeFilters] = useState({
    status: '',
    role: '',
    designation: '',
    state: '',
    district: ''
  });
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const [showRegistrationDetailModal, setShowRegistrationDetailModal] = useState(false);
  const [selectedRegistrationForDetail, setSelectedRegistrationForDetail] = useState(null);

  useEffect(() => {
    fetchData();

    // Listen for KYC status updates from Employee Dashboard
    const handleKYCUpdate = (event) => {
      console.log('ðŸ”„ Super Admin Dashboard: KYC status updated, refreshing data...');
      console.log('ðŸ“Š KYC Update details:', event.detail);
      // Wait 2 seconds for backend to update, then refresh
      setTimeout(() => {
        console.log('ðŸ”„ Refreshing Super Admin data after KYC update...');
        fetchData();
      }, 2000);
    };

    window.addEventListener('kycStatusUpdated', handleKYCUpdate);

    return () => {
      window.removeEventListener('kycStatusUpdated', handleKYCUpdate);
    };
  }, []);

  // Debug effect to monitor farmers state
  useEffect(() => {
    if (farmers) {
      console.log('Farmers state updated:', farmers);
      console.log('Farmers count:', farmers.length);
    }
  }, [farmers]);

  // Debug effect to monitor employees state
  useEffect(() => {
    console.log('Employees state updated:', employees);
    console.log('Employees count:', employees?.length || 0);
    if (employees && employees.length > 0) {
      console.log('First employee:', employees[0]);
    }
  }, [employees]);

  const fetchData = async () => {
    try {
      setLoading(true);
      console.log('ðŸ”„ Fetching real-time data from backend APIs...');

      let farmersData, employeesData, registrationsData;

      try {
        console.log('ðŸ“¡ Making API calls to fetch real data...');
        [farmersData, employeesData, registrationsData] = await Promise.all([
          farmersAPI.getAllFarmers(), // Use direct farmers API
          employeesAPI.getAllEmployees(),
          superAdminAPI.getRegistrationList()
        ]);

        console.log('âœ… API calls completed successfully');
        console.log('ðŸ“Š Farmers data received:', farmersData?.length || 0, 'records');
        console.log('ðŸ“Š Employees data received:', employeesData?.length || 0, 'records');
        console.log('ðŸ“Š Registrations data received:', registrationsData?.length || 0, 'records');

        // Log first farmer data structure to debug field mapping
        if (farmersData && farmersData.length > 0) {
          console.log('ðŸ” First farmer data structure:', farmersData[0]);
          console.log('ðŸ” Available fields:', Object.keys(farmersData[0]));
        }

        // Log employee data structure to debug dropdown
        if (employeesData && employeesData.length > 0) {
          console.log('ðŸ” First employee data structure:', employeesData[0]);
          console.log('ðŸ” Available employee fields:', Object.keys(employeesData[0]));
          console.log('ðŸ” Employee names in dropdown:', employeesData.map(emp => emp.name));
        }
      } catch (apiError) {
        console.error('âŒ API call failed:', apiError);
        console.error('âŒ API Error details:', apiError.response?.data || apiError.message);
        console.error('âŒ API Error status:', apiError.response?.status);
        console.error('âŒ API Error URL:', apiError.config?.url);

        // Set empty arrays if API fails
        farmersData = [];
        employeesData = [];
        registrationsData = [];

        // Show user-friendly error message
        setError('Failed to fetch data from server. Please check your connection and try again.');
      }

      console.log('ðŸ“‹ Raw API responses:');
      console.log('Farmers data:', farmersData);
      console.log('Farmers data length:', farmersData?.length || 0);
      console.log('First farmer structure:', farmersData?.[0]);
      console.log('Employees data:', employeesData);
      console.log('Employees data length:', employeesData?.length || 0);
      console.log('First employee structure:', employeesData?.[0]);
      console.log('Registrations data:', registrationsData);

      // Use real API data only - no mock data
      const finalFarmersData = farmersData || [];
      const finalEmployeesData = employeesData || [];
      const finalRegistrationsData = registrationsData || [];

      console.log('ðŸŽ¯ Final data to be used:');
      console.log('Final farmers count:', finalFarmersData.length);
      console.log('Final employees count:', finalEmployeesData.length);
      console.log('Final registrations count:', finalRegistrationsData.length);

      if (finalFarmersData.length === 0) {
        console.log('âš ï¸ No farmers data available from API');
        setError('No farmers data available. Please check if the backend is running and has data.');
      }

      if (finalEmployeesData.length === 0) {
        console.log('âš ï¸ No employees data available from API');
      }

      if (finalRegistrationsData.length === 0) {
        console.log('âš ï¸ No registrations data available from API');
      }

      setFarmers(finalFarmersData);
      setEmployees(finalEmployeesData);
      setRegistrations(finalRegistrationsData);

      console.log('âœ… Data set successfully');
      console.log('Final farmers count:', finalFarmersData.length);
      console.log('Final employees count:', finalEmployeesData.length);
      console.log('Final registrations count:', finalRegistrationsData.length);

    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const getFilteredFarmers = () => {
    return (farmers || []).filter(farmer => {
      const matchesState = !filters.state || farmer.state === filters.state;
      const matchesDistrict = !filters.district || farmer.district === filters.district;
      const matchesKycStatus = !filters.kycStatus || farmer.kycStatus === filters.kycStatus;

      // Debug employee filter
      if (filters.employeeFilter) {
        console.log('ðŸ” Employee filter active:', filters.employeeFilter);
        console.log('ðŸ” Farmer assignedEmployee field:', farmer.assignedEmployee);
        console.log('ðŸ” Available farmer fields:', Object.keys(farmer));
      }

      // More robust employee filter matching
      const matchesEmployee = !filters.employeeFilter || 
        (farmer.assignedEmployee && 
         (farmer.assignedEmployee === filters.employeeFilter || 
          farmer.assignedEmployee.toLowerCase().includes(filters.employeeFilter.toLowerCase()) ||
          filters.employeeFilter.toLowerCase().includes(farmer.assignedEmployee.toLowerCase())
         ));

      // Debug: Log all farmers and their assigned employees when filter is active
      if (filters.employeeFilter) {
        console.log('ðŸ” All farmers and their assigned employees:');
        (farmers || []).forEach((f, index) => {
          console.log(`  Farmer ${index + 1}: ${f.name} -> Assigned to: "${f.assignedEmployee}"`);
        });
      }

      return matchesState && matchesDistrict && matchesKycStatus && matchesEmployee;
    });
  };

  const getFilteredEmployees = () => {
    return (employees || []).filter(employee => {
      const matchesStatus = !employeeFilters.status || employee.status === employeeFilters.status;
      const matchesRole = !employeeFilters.role || employee.role === employeeFilters.role;
      const matchesDesignation = !employeeFilters.designation || employee.designation === employeeFilters.designation;
      const matchesState = !employeeFilters.state || employee.state === employeeFilters.state;
      const matchesDistrict = !employeeFilters.district || employee.district === employeeFilters.district;

      return matchesStatus && matchesRole && matchesDesignation && matchesState && matchesDistrict;
    });
  };

  const getFilteredRegistrations = () => {
    console.log('All registrations:', registrations);
    // Apply filters
    const filtered = registrations.filter(registration => {
      const roleMatch = !registrationFilters.role || registration.role === registrationFilters.role;
      const statusMatch = !registrationFilters.status || registration.status === registrationFilters.status;
      return roleMatch && statusMatch;
    });
    console.log('Filtered registrations:', filtered);
    return filtered;
  };

  const getStats = () => {
    const totalFarmers = farmers.length;
    const totalEmployees = employees.length;
    const pendingRegistrations = registrations.filter(r => {
      const status = r.status || r.userStatus || r.accessStatus;
      return status === 'PENDING' || status === 'pending' || status === 'Pending';
    }).length;
    const unassignedFarmers = farmers.filter(f => f.accessStatus === 'PENDING').length;
    const activeEmployees = employees.filter(e => e.status === 'ACTIVE').length;
    const totalFPO = 0; // Placeholder for FPO count

    return {
      totalFarmers,
      totalEmployees,
      pendingRegistrations,
      unassignedFarmers,
      activeEmployees,
      totalFPO
    };

  };

  const handleViewRegistration = (registration) => {
    setSelectedRegistrationForDetail(registration);
    setShowRegistrationDetailModal(true);
  };

  const handleCloseRegistrationDetailModal = () => {
    setShowRegistrationDetailModal(false);
    setSelectedRegistrationForDetail(null);
  };

  const handleRegistrationUpdate = () => {
    // Refresh the registration data
    fetchData();
  };

  const handleApproveRegistration = async (registrationId) => {
    try {
      await superAdminAPI.approveUser(registrationId);
      setRegistrations(prev => prev.map(reg => 
        reg.id === registrationId ? { ...reg, status: 'APPROVED' } : reg
      ));
      alert('Registration approved successfully!');
    } catch (error) {
      console.error('Error approving registration:', error);
      alert('Failed to approve registration');
    }
  };

  const handleRejectRegistration = async (registrationId) => {
    try {
      await superAdminAPI.rejectUser(registrationId);
      setRegistrations(prev => prev.map(reg => 
        reg.id === registrationId ? { ...reg, status: 'REJECTED' } : reg
      ));
      alert('Registration rejected successfully!');
    } catch (error) {
      console.error('Error rejecting registration:', error);
      alert('Failed to reject registration');
    }
  };

  const handleViewFarmer = (farmer) => {
    // Helper function to safely get values with fallbacks
    const safeGet = (value, fallback = '') => {
      if (value === null || value === undefined || value === '') {
        return fallback;
      }
      return value;
    };

    // Transform farmer data to match complete DTO structure
    const farmerData = {
      id: farmer.id,

      // Personal Information
      salutation: safeGet(farmer.salutation),
      firstName: safeGet(farmer.firstName || farmer.name?.split(' ')[0]),
      middleName: safeGet(farmer.middleName || farmer.name?.split(' ')[1]),
      lastName: safeGet(farmer.lastName || farmer.name?.split(' ').slice(-1)[0]),
      dateOfBirth: safeGet(farmer.dateOfBirth || farmer.dob),
      gender: safeGet(farmer.gender),
      fatherName: safeGet(farmer.fatherName),
      contactNumber: safeGet(farmer.contactNumber || farmer.phone),
      alternativeRelationType: safeGet(farmer.alternativeRelationType),
      alternativeContactNumber: safeGet(farmer.alternativeContactNumber),
      nationality: safeGet(farmer.nationality, 'Indian'),
      email: safeGet(farmer.email),

      // Address Information
      country: safeGet(farmer.country, 'India'),
      state: safeGet(farmer.state),
      district: safeGet(farmer.district),
      block: safeGet(farmer.block),
      village: safeGet(farmer.village),
      pincode: safeGet(farmer.pincode || farmer.zipcode),

      // Education & Experience
      education: safeGet(farmer.education),
      experience: safeGet(farmer.experience || farmer.farmingExperience),

      // Current Crop Information
      cropPhoto: safeGet(farmer.cropPhoto),
      currentSurveyNumber: safeGet(farmer.currentSurveyNumber),
      currentLandHolding: farmer.currentLandHolding || farmer.landArea || '',
      currentGeoTag: safeGet(farmer.currentGeoTag),
      currentCrop: safeGet(farmer.currentCrop || farmer.primaryCrop),
      currentNetIncome: farmer.currentNetIncome || farmer.annualIncome || '',
      currentSoilTest: farmer.currentSoilTest || false,

      // Proposed Crop Information
      proposedSurveyNumber: safeGet(farmer.proposedSurveyNumber),
      proposedLandHolding: farmer.proposedLandHolding || '',
      proposedGeoTag: safeGet(farmer.proposedGeoTag),
      proposedCrop: safeGet(farmer.proposedCrop || farmer.secondaryCrop),
      proposedNetIncome: farmer.proposedNetIncome || '',
      proposedSoilTest: farmer.proposedSoilTest || false,
      proposedSoilTestCertificate: safeGet(farmer.proposedSoilTestCertificate),

      // Water/Irrigation Information
      currentWaterSource: safeGet(farmer.currentWaterSource || farmer.irrigationType),
      currentDischargeLPH: safeGet(farmer.currentDischargeLPH),
      currentSummerDischarge: safeGet(farmer.currentSummerDischarge),
      currentBorewellLocation: safeGet(farmer.currentBorewellLocation),
      proposedWaterSource: safeGet(farmer.proposedWaterSource),
      proposedDischargeLPH: safeGet(farmer.proposedDischargeLPH),
      proposedSummerDischarge: safeGet(farmer.proposedSummerDischarge),
      proposedBorewellLocation: safeGet(farmer.proposedBorewellLocation),

      // Banking Information
      bankName: safeGet(farmer.bankName),
      accountNumber: safeGet(farmer.accountNumber),
      branchName: safeGet(farmer.branchName),
      ifscCode: safeGet(farmer.ifscCode),

      // Document Information
      documentType: safeGet(farmer.documentType),
      aadhaarNumber: safeGet(farmer.aadhaarNumber),
      panNumber: safeGet(farmer.panNumber),
      voterId: safeGet(farmer.voterId),
      rationCardNumber: safeGet(farmer.rationCardNumber),

      // File attachments
      photoFileName: safeGet(farmer.photoFileName),
      passbookFileName: safeGet(farmer.passbookFileName),
