import React, { useState, useRef } from 'react';
import { apiService } from '../api/apiService';
import '../styles/BulkOperations.css';

const BulkOperations = ({ userRole }) => {
  const [activeTab, setActiveTab] = useState('import');
  const [importType, setImportType] = useState('FARMER');
  const [selectedFile, setSelectedFile] = useState(null);
  const [importStatus, setImportStatus] = useState(null);
  const [isImporting, setIsImporting] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [exportFilters, setExportFilters] = useState({
    format: 'EXCEL',
    assignedEmployeeEmail: '',
    location: '',
    kycStatus: '',
    fromDate: '',
    toDate: ''
  });
  const [assignmentData, setAssignmentData] = useState({
    farmerIds: [],
    employeeId: '',
    assignmentType: 'MANUAL'
  });
  const fileInputRef = useRef();

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      const allowedTypes = [
        'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
        'application/vnd.ms-excel',
        'text/csv'
      ];
      
      if (allowedTypes.includes(file.type) || file.name.endsWith('.csv')) {
        setSelectedFile(file);
      } else {
        alert('Please select a valid Excel (.xlsx, .xls) or CSV file.');
        event.target.value = '';
      }
    }
  };

  const downloadTemplate = async (type) => {
    try {
      const response = await apiService.downloadTemplate(type.toLowerCase());
      const blob = new Blob([response], { 
        type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' 
      });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${type.toLowerCase()}_import_template.xlsx`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error('Error downloading template:', error);
      alert('Failed to download template');
    }
  };

  const handleImport = async () => {
    if (!selectedFile) {
      alert('Please select a file to import');
      return;
    }

    setIsImporting(true);
    try {
      const formData = new FormData();
      formData.append('file', selectedFile);
      formData.append('autoAssign', false);
      formData.append('assignmentStrategy', 'MANUAL');

      const response = await apiService.bulkImport(importType.toLowerCase(), formData);
      setImportStatus(response);
      
      if (response.status === 'PROCESSING') {
        // Poll for status updates
        pollImportStatus(response.importId);
      }
    } catch (error) {
      console.error('Import error:', error);
      alert('Import failed: ' + error.message);
    } finally {
      setIsImporting(false);
    }
  };

  const pollImportStatus = async (importId) => {
    const maxAttempts = 30; // 5 minutes with 10-second intervals
    let attempts = 0;

    const poll = async () => {
      try {
        const status = await apiService.getImportStatus(importId);
        setImportStatus(status);

        if (status.status === 'COMPLETED' || status.status === 'FAILED') {
          return;
        }

        attempts++;
        if (attempts < maxAttempts) {
          setTimeout(poll, 10000); // Poll every 10 seconds
        }
      } catch (error) {
        console.error('Error polling import status:', error);
      }
    };

    setTimeout(poll, 10000);
  };

  const handleExport = async () => {
    setIsExporting(true);
    try {
      const response = await apiService.bulkExport(importType.toLowerCase(), exportFilters);
      
      const blob = new Blob([response], { 
        type: exportFilters.format === 'EXCEL' 
          ? 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
          : 'text/csv'
      });
      
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${importType.toLowerCase()}_export_${Date.now()}.${exportFilters.format.toLowerCase()}`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error('Export error:', error);
      alert('Export failed: ' + error.message);
    } finally {
      setIsExporting(false);
    }
  };

  const handleBulkAssignment = async () => {
    if (!assignmentData.farmerIds.length || !assignmentData.employeeId) {
      alert('Please select farmers and an employee for assignment');
      return;
    }

    try {
      let response;
      switch (assignmentData.assignmentType) {
        case 'MANUAL':
          response = await apiService.bulkAssignFarmersToEmployee(
            assignmentData.farmerIds, 
            assignmentData.employeeId
          );
          break;
        case 'LOCATION':
          response = await apiService.bulkAssignFarmersByLocation(
            assignmentData.location, 
            assignmentData.employeeId
          );
          break;
        case 'ROUND_ROBIN':
          response = await apiService.bulkAssignFarmersRoundRobin(
            assignmentData.farmerIds
          );
          break;
        default:
          throw new Error('Invalid assignment type');
      }
      
      alert('Bulk assignment completed successfully');
    } catch (error) {
      console.error('Bulk assignment error:', error);
      alert('Bulk assignment failed: ' + error.message);
    }
  };

  return (
    <div className="bulk-operations">
      <div className="bulk-header">
        <h2>Bulk Operations</h2>
        <p>Import and export large datasets efficiently</p>
      </div>

      <div className="bulk-tabs">
        <button 
          className={`tab ${activeTab === 'import' ? 'active' : ''}`}
          onClick={() => setActiveTab('import')}
        >
          📥 Bulk Import
        </button>
        <button 
          className={`tab ${activeTab === 'export' ? 'active' : ''}`}
          onClick={() => setActiveTab('export')}
        >
          📤 Bulk Export
        </button>
        <button 
          className={`tab ${activeTab === 'assignment' ? 'active' : ''}`}
          onClick={() => setActiveTab('assignment')}
        >
          👥 Bulk Assignment
        </button>
      </div>

      <div className="bulk-content">
        {activeTab === 'import' && (
          <div className="import-section">
            <div className="section-header">
              <h3>Bulk Import</h3>
              <div className="template-downloads">
                <button 
                  className="template-btn"
                  onClick={() => downloadTemplate('FARMER')}
                >
                  📄 Download Farmer Template
                </button>
                {userRole === 'SUPER_ADMIN' && (
                  <button 
                    className="template-btn"
                    onClick={() => downloadTemplate('EMPLOYEE')}
                  >
                    📄 Download Employee Template
                  </button>
                )}
              </div>
            </div>

            <div className="import-form">
              <div className="form-group">
                <label>Import Type:</label>
                <select 
                  value={importType} 
                  onChange={(e) => setImportType(e.target.value)}
                >
                  <option value="FARMER">Farmer Data</option>
                  {userRole === 'SUPER_ADMIN' && (
                    <option value="EMPLOYEE">Employee Data</option>
                  )}
                </select>
              </div>

              <div className="form-group">
                <label>Select File:</label>
                <input
                  type="file"
                  ref={fileInputRef}
                  onChange={handleFileSelect}
                  accept=".xlsx,.xls,.csv"
                  className="file-input"
                />
                {selectedFile && (
                  <p className="file-info">
                    Selected: {selectedFile.name} ({(selectedFile.size / 1024 / 1024).toFixed(2)} MB)
                  </p>
                )}
              </div>

              <button 
                className="import-btn"
                onClick={handleImport}
                disabled={!selectedFile || isImporting}
              >
                {isImporting ? '🔄 Importing...' : '📥 Start Import'}
              </button>
            </div>

            {importStatus && (
              <div className={`import-status ${importStatus.status.toLowerCase()}`}>
                <h4>Import Status: {importStatus.status}</h4>
                <div className="status-details">
                  <p><strong>Total Records:</strong> {importStatus.totalRecords || 0}</p>
                  <p><strong>Successful:</strong> {importStatus.successfulImports || 0}</p>
                  <p><strong>Failed:</strong> {importStatus.failedImports || 0}</p>
                  <p><strong>Skipped:</strong> {importStatus.skippedRecords || 0}</p>
                  {importStatus.message && (
                    <p><strong>Message:</strong> {importStatus.message}</p>
                  )}
                </div>
                
                {importStatus.errors && importStatus.errors.length > 0 && (
                  <div className="import-errors">
                    <h5>Errors ({importStatus.errors.length}):</h5>
                    <div className="error-list">
                      {importStatus.errors.slice(0, 10).map((error, index) => (
                        <div key={index} className="error-item">
                          <span>Row {error.rowNumber}: {error.fieldName}</span>
                          <span>{error.errorMessage}</span>
                        </div>
                      ))}
                      {importStatus.errors.length > 10 && (
                        <p>... and {importStatus.errors.length - 10} more errors</p>
                      )}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        )}

        {activeTab === 'export' && (
          <div className="export-section">
            <div className="section-header">
              <h3>Bulk Export</h3>
            </div>

            <div className="export-form">
              <div className="form-group">
                <label>Export Type:</label>
                <select 
                  value={importType} 
                  onChange={(e) => setImportType(e.target.value)}
                >
                  <option value="FARMER">Farmer Data</option>
                  {userRole === 'SUPER_ADMIN' && (
                    <option value="EMPLOYEE">Employee Data</option>
                  )}
                </select>
              </div>

              <div className="form-group">
                <label>Format:</label>
                <select 
                  value={exportFilters.format} 
                  onChange={(e) => setExportFilters({
                    ...exportFilters, 
                    format: e.target.value
                  })}
                >
                  <option value="EXCEL">Excel (.xlsx)</option>
                  <option value="CSV">CSV (.csv)</option>
                </select>
              </div>

              <div className="form-group">
                <label>Assigned Employee Email:</label>
                <input
                  type="email"
                  value={exportFilters.assignedEmployeeEmail}
                  onChange={(e) => setExportFilters({
                    ...exportFilters, 
                    assignedEmployeeEmail: e.target.value
                  })}
                  placeholder="Filter by assigned employee"
                />
              </div>

              <div className="form-group">
                <label>Location (District):</label>
                <input
                  type="text"
                  value={exportFilters.location}
                  onChange={(e) => setExportFilters({
                    ...exportFilters, 
                    location: e.target.value
                  })}
                  placeholder="Filter by district"
                />
              </div>

              <div className="form-group">
                <label>KYC Status:</label>
                <select 
                  value={exportFilters.kycStatus} 
                  onChange={(e) => setExportFilters({
                    ...exportFilters, 
                    kycStatus: e.target.value
                  })}
                >
                  <option value="">All Statuses</option>
                  <option value="PENDING">Pending</option>
                  <option value="APPROVED">Approved</option>
                  <option value="REJECTED">Rejected</option>
                  <option value="REFER_BACK">Refer Back</option>
                </select>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>From Date:</label>
                  <input
                    type="date"
                    value={exportFilters.fromDate}
                    onChange={(e) => setExportFilters({
                      ...exportFilters, 
                      fromDate: e.target.value
                    })}
                  />
                </div>
                <div className="form-group">
                  <label>To Date:</label>
                  <input
                    type="date"
                    value={exportFilters.toDate}
                    onChange={(e) => setExportFilters({
                      ...exportFilters, 
                      toDate: e.target.value
                    })}
                  />
                </div>
              </div>

              <button 
                className="export-btn"
                onClick={handleExport}
                disabled={isExporting}
              >
                {isExporting ? '🔄 Exporting...' : '📤 Export Data'}
              </button>
            </div>
          </div>
        )}

        {activeTab === 'assignment' && (
          <div className="assignment-section">
            <div className="section-header">
              <h3>Bulk Assignment</h3>
            </div>

            <div className="assignment-form">
              <div className="form-group">
                <label>Assignment Type:</label>
                <select 
                  value={assignmentData.assignmentType} 
                  onChange={(e) => setAssignmentData({
                    ...assignmentData, 
                    assignmentType: e.target.value
                  })}
                >
                  <option value="MANUAL">Manual Assignment</option>
                  <option value="LOCATION">By Location</option>
                  <option value="ROUND_ROBIN">Round Robin</option>
                </select>
              </div>

              {assignmentData.assignmentType === 'MANUAL' && (
                <div className="form-group">
                  <label>Farmer IDs (comma-separated):</label>
                  <input
                    type="text"
                    value={assignmentData.farmerIds.join(',')}
                    onChange={(e) => setAssignmentData({
                      ...assignmentData, 
                      farmerIds: e.target.value.split(',').map(id => id.trim()).filter(id => id)
                    })}
                    placeholder="1, 2, 3, 4, 5"
                  />
                </div>
              )}

              {assignmentData.assignmentType === 'LOCATION' && (
                <div className="form-group">
                  <label>Location (District):</label>
                  <input
                    type="text"
                    value={assignmentData.location}
                    onChange={(e) => setAssignmentData({
                      ...assignmentData, 
                      location: e.target.value
                    })}
                    placeholder="Enter district name"
                  />
                </div>
              )}

              {assignmentData.assignmentType === 'ROUND_ROBIN' && (
                <div className="form-group">
                  <label>Farmer IDs (comma-separated):</label>
                  <input
                    type="text"
                    value={assignmentData.farmerIds.join(',')}
                    onChange={(e) => setAssignmentData({
                      ...assignmentData, 
                      farmerIds: e.target.value.split(',').map(id => id.trim()).filter(id => id)
                    })}
                    placeholder="1, 2, 3, 4, 5"
                  />
                </div>
              )}

              {assignmentData.assignmentType !== 'ROUND_ROBIN' && (
                <div className="form-group">
                  <label>Employee ID:</label>
                  <input
                    type="number"
                    value={assignmentData.employeeId}
                    onChange={(e) => setAssignmentData({
                      ...assignmentData, 
                      employeeId: e.target.value
                    })}
                    placeholder="Enter employee ID"
                  />
                </div>
              )}

              <button 
                className="assignment-btn"
                onClick={handleBulkAssignment}
              >
                👥 Assign Farmers
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default BulkOperations;
